'use strict';
const path = require('path');
const fs = require('fs');
const os = require('os');
const assert = require('assert');
const utils = require('node-tool-utils');
const merge = require('webpack-merge');

exports.getCompileTempDir = (project, filename = '') => {
  return path.join(os.tmpdir(), 'easywebpack', project, filename);
};

exports.getEasyInfoFilepath = project => {
  return exports.getCompileTempDir(project, 'easy.json');
};

exports.getEasyInfo = project => {
  if (!project) {
    const pkgfile = path.join(process.cwd(), 'package.json');
    const pkg = require(pkgfile);
    project = pkg.package || pkg.name;
  }
  const filepath = exports.getEasyInfoFilepath(project);
  if (fs.existsSync(filepath)) {
    return utils.readFile(filepath);
  }
  return {};
};

exports.setEasyInfo = (json, clear = false) => {
  const pkgfile = path.join(process.cwd(), 'package.json');
  const pkg = require(pkgfile);
  const project = pkg.package || pkg.name;
  assert(project, 'package.json file missing name config');
  const filepath = exports.getEasyInfoFilepath(project);
  if (clear) {
    utils.writeFile(filepath, json);
  } else {
    const easyInfo = exports.getEasyInfo(project);
    const info = merge(easyInfo, json);
    utils.writeFile(filepath, info);
  }
};